

var App = require('../app'),
    util = require('util'),
    PATH = require('path');

var ExportableApp = module.exports = function (name, params, manifest) {
    App.call(this, name, params, manifest);
};

util.inherits(ExportableApp, App);

(function () {
    
    this.customFormatter = function (res, e, context, callback) {        
         var formatter= [];    
         callback(null, formatter);       
    }
}).call(ExportableApp.prototype);
    
(function () {
    
   this.fileInfo = function (res, e, context, callback) {        
        callback(null, {
            "name":"Position Data"
        });
    }
}).call(ExportableApp.prototype);

(function () {
    
   this.sheets = function (res, e, context, callback) {               
        var sheets = [];
        callback(null, sheets);
    }
}).call(ExportableApp.prototype);

