

var ExportableApp = require('./app'),
    util = require('util'),
    PATH = require('path');

var Sheet = module.exports = function (name) {    
    console.log(ExportableApp)
    ExportableApp.call(this, name);
};

util.inherits(Sheet, ExportableApp);

// generate excel export API
(function () {

    this.dataSection = function (res, e, context, callback) {
       
        var dataSections =[];
        
        callback(null, dataSections);
    }
}).call(Sheet.prototype);
 
    
(function () {
    
    this.pivotSection = function (res, e, context, callback) {        
        var pivotSections= [];
        callback(null, pivotSections);       
    }
}).call(Sheet.prototype);


