
var Widget = require('ds-widget'),
    util = require('util'),
    FS = require('fs');


var GridWidget = module.exports = function (name, params, manifest) {

    // console.log('grid params')
    // require('eyes').inspect(params)

    Widget.call(this, name, params, manifest);
}

util.inherits(GridWidget, Widget);

(function () {
    this.data = function (res, e, context, callback) {

        callback(null, {
            id: 'a' + require('node-uuid').v1()
        })
    };
    this.folder = function (res, e, context, callback) {
        return __dirname;
    };
}).call(GridWidget.prototype);
