
var Tile = require('ds-ui-block').Tile,
    util = require('util'),
    FS = require('fs'),
    PATH = require('path');

var Widget = module.exports = function (name, params, manifest) {

    Tile.call(this, name, params, manifest);
}

util.inherits(Widget, Tile); 

// raw package json
(function () {

    this.getPackageJSON = function (callback) {

        FS.readFile(PATH.join(this.folder(), 'package.json'), 'utf8', function (err, data) {

            if (err)
                return callback(err);

            callback(null, JSON.parse(data));
        });
    };
}).call(Widget.prototype);